def is_prime(n):
    if n < 2:
        return False
    for i in range(2, int(n ** 0.5) + 1):
        if n % i == 0:
            return False
    return True
def n_primes_after_number(number, n):
    count = 0
    current_number = number + 1
    primes = []
    while count < n:
        if is_prime(current_number):
            primes.append(current_number)
            count += 1
        current_number += 1
    return primes
